package com.kalos.visual.controles.textos.txtarea;import java.awt.BorderLayout;import javax.swing.*;public class AreaTextoVertical extends JPanel {    private JScrollPane B;    private JTextArea A;    public AreaTextoVertical() {	B = new JScrollPane(20, 31);	A = new JTextArea();	setLayout(new BorderLayout());	B.getViewport().add(A);	A.setLineWrap(true);	A.setWrapStyleWord(true);	add(B);    }    public void setText(String s) {	A.setText(s);    }    public String getText() {	return A.getText();    }}