package com.kalos.enumeraciones;import com.kalos.recursos.Recursos;public enum Modo {	Indicativo(1, "indicativo"), Subjuntivo(2, "subjuntivo"), Optativo(3, "optativo"), Imperativo(4, "imperativo");	    private int valorEntero;    private String etiquetaRecursos;        Modo (int valorEntero, String etiquetaRecursos){        this.valorEntero = valorEntero;        this.etiquetaRecursos = etiquetaRecursos;    }        public String toString(){        return Recursos.getCadena(this.etiquetaRecursos);    }        public int valorEntero(){        return this.valorEntero;    }               public static Modo getEnum(int valorEntero){        for (Modo enu : values()) {            if (enu.valorEntero==valorEntero){                return enu;            }        }        throw new RuntimeException("no hay enum de modo para el n√∫mero " + valorEntero);    } 		}