package com.kalos.enumeraciones;import com.kalos.recursos.Recursos;public enum Caso {	Nominativo(1, "nominativo"), Vocativo(2, "vocativo"), Acusativo(3, "acusativo"), Genitivo(4, "genitivo"), Dativo(5, "dativo");	    private int valorEntero;    private String etiquetaRecursos;        Caso (int valorEntero, String etiquetaRecursos){        this.valorEntero = valorEntero;        this.etiquetaRecursos = etiquetaRecursos;    }        public String toString(){        return Recursos.getCadena(this.etiquetaRecursos);    }        public int valorEntero(){        return this.valorEntero;    }               public static Caso getEnum(int valorEntero){        for (Caso enu : values()) {            if (enu.valorEntero==valorEntero){                return enu;            }        }        throw new RuntimeException("no hay enum de caso para el n√∫mero " + valorEntero);    } 		}