package kalos.enumeraciones;public enum Modo {	Indicativo, Subjuntivo, Optativo, Imperativo;		public static int getInt(Modo valor){		switch(valor){		case Indicativo:			return 1;		case Subjuntivo:			return 2;		case Optativo:			return 3;		case Imperativo:		default:			return 0;		}	}		public static Modo getEnum(int valor){		switch(valor){		  case 1:			  return Indicativo;		  case 2:			  return Subjuntivo;		  case 3:			  return Optativo;		  case 4:			  return Imperativo;		  default:			  throw new RuntimeException("no hay Modo n√∫mero " + valor);		}	}		}