package kalos.flexion.conjugacion.negocio;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import kalos.enumeraciones.FuerteDebil;import kalos.enumeraciones.Modo;import kalos.enumeraciones.Persona;import kalos.enumeraciones.Tiempo;import kalos.enumeraciones.Voz;public class Ocurrencia {    private Map<CoordenadasOc, List<String>> mapOcurrencias = new HashMap<CoordenadasOc, List<String>>();    public List<CoordenadasOc> getCoordenadas() {	return new ArrayList<CoordenadasOc>(this.mapOcurrencias.keySet());    }    public List<String> getFormas(Voz paramZ, Modo paramp, Tiempo paramj, FuerteDebil paramP, Persona param_) {	CoordenadasOc localB = new CoordenadasOc(paramZ, paramp, paramj, paramP, param_);	return getFormas(localB);    }    public List<String> getFormas(CoordenadasOc paramB) {	List<String> localList = this.mapOcurrencias.get(paramB);	return localList;    }    public void borraFormas(CoordenadasOc paramB) {	this.mapOcurrencias.remove(paramB);    }    public void borraFormas(Voz paramZ, Modo paramp, Tiempo paramj, FuerteDebil paramP, Persona param_) {	CoordenadasOc localB = new CoordenadasOc(paramZ, paramp, paramj, paramP, param_);	borraFormas(localB);    }    public final void agregaFormaIndividual(Voz paramZ, Modo paramp, Tiempo paramj, FuerteDebil paramP, Persona param_,	    String paramString, int paramInt) {	CoordenadasOc localB = new CoordenadasOc(paramZ, paramp, paramj, paramP, param_);	List<String> localObject = this.mapOcurrencias.get(localB);	if (localObject == null) {	    localObject = new ArrayList<String>();	}	if (paramInt < localObject.size()) {	    localObject.set(paramInt, paramString);	} else {	    while (paramInt >= localObject.size()) {		localObject.add(null);	    }	    localObject.set(paramInt, paramString);	}	this.mapOcurrencias.put(localB, localObject);    }    public final int getCantidadDeFormas(Voz paramZ, Modo paramp, Tiempo paramj, FuerteDebil paramP, Persona param_) {	List<String> localList = getFormas(paramZ, paramp, paramj, paramP, param_);	if (localList != null) {	    return localList.size();	}	return 0;    }    public final String getFormaIndividual(Voz paramZ, Modo paramp, Tiempo paramj, FuerteDebil paramP, Persona param_,	    int paramInt) {	List<String> localList = getFormas(paramZ, paramp, paramj, paramP, param_);	if (localList == null) {	    return null;	}	return (String) localList.get(paramInt);    }    public void limpiar() {	this.mapOcurrencias.clear();    }    public int getCantidadFormas() {	return this.mapOcurrencias.entrySet().size();    }}